import React from 'react';

export default function Produtos() {

    const data = [
        {
            "Id": 0,
            "Imagem": "https://anba.com.br/wp-content/uploads/home/fh_04sk4m0258.jpg",
            "Descricao": "Cavalo Árabe: Conhecido por sua resistência e velocidade, o Cavalo Árabe é uma das raças mais antigas e influentes no desenvolvimento de outras raças. Eles são facilmente reconhecíveis por sua cabeça distinta e orelhas pequenas."
        },
        {
            "Id": 1,
            "Imagem": "https://www.comprerural.com/wp-content/uploads/2024/05/raca-de-cavalo-mais-usada-do-mundo-750x430.jpg",
            "Descricao": "Puro Sangue Inglês: Esta raça é famosa por sua velocidade e agilidade, sendo frequentemente usada em corridas de cavalos. Eles têm um corpo esbelto e musculoso, e são conhecidos por sua grande resistência."
        },
        {
            "Id": 2,
            "Imagem": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ-WPXpwyH1L2-pfMUmTt4S8Pfmig1P5lA4CdueAdERbYFdcK-R6Ahl6xS-JcJ7OsTnCGQ&usqp=CAU",
            "Descricao": "Cavalo Clydesdale: Originário da Escócia, o Clydesdale é uma raça de cavalo de trabalho conhecida por seu tamanho imponente e força. Eles são frequentemente usados em trabalhos agrícolas e têm uma pelagem distinta, geralmente de cor castanho-avermelhada com marcas brancas."
        },
        {
            "Id": 3,
            "Imagem": "https://s2-globorural.glbimg.com/CuC-OpZsVKrI3tipEH2iEYvG7iU=/0x0:1080x810/924x0/smart/filters:strip_icc()/i.s3.glbimg.com/v1/AUTH_afe5c125c3bb42f0b5ae633b58923923/internal_photos/bs/2023/Y/Y/mL78GBRT6iPOlbZdT5nA/snapinsta.app-369001010-669435598395389-4305361386627724147-n-1080-1-.jpg",
            "Descricao": "Cavalo Percheron: Outra raça de trabalho, o Percheron é conhecido por sua força e resistência. Originário da França, esses cavalos são frequentemente usados para puxar cargas pesadas e têm uma pelagem que pode variar de cinza a preto."
        },
        {
            "Id": 4,
            "Imagem": "https://files.agro20.com.br/uploads/2020/11/cavalo-mustang-3.jpg",
            "Descricao": "Cavalo Mustang: Uma raça selvagem que habita a América do Norte, os Mustangs são conhecidos por sua resistência e habilidade de sobrevivência. Eles têm uma aparência robusta e são adaptáveis a diferentes ambientes."
        },
        {
            "Id": 5,
            "Imagem": "https://luanasantanape.wordpress.com/wp-content/uploads/2015/05/image11.jpg",
            "Descricao": "Cavalo Shire: Uma das maiores raças de cavalos de trabalho, o Shire é conhecido por seu tamanho impressionante e força. Originário da Grã-Bretanha, eles têm uma pelagem geralmente preta, castanha ou cinza e são usados para tarefas pesadas."
        },
        {
            "Id": 6,
            "Imagem": "https://univitta.net/backend/web/uploads/blog/posts/appaloosa_20210112152359.png",
            "Descricao": "Cavalo Appaloosa: Reconhecível por sua pelagem manchada, o Appaloosa é uma raça americana com uma rica história de trabalho e competições. Eles são conhecidos por sua resistência e personalidade amigável."
        },
        {
            "Id": 7,
            "Imagem": "https://univitta.net/backend/web/uploads/blog/posts/andaluz_20210104145352.png",
            "Descricao": "Cavalo Andaluz: Originário da Espanha, o Andaluz é famoso por sua beleza e habilidades em competições de alta escola. Eles têm uma pelagem geralmente branca ou cinza e são conhecidos por seu estilo elegante e movimentos fluidos."
        },
        {
            "Id": 8,
            "Imagem": "https://v18.clubedohipismo.com.br/wp-content/uploads/2018/03/Templebready-Fear-Bui-Connemara-fonte-Horse-Hound.jpg",
            "Descricao": "Cavalo Connemara: Uma raça irlandesa, o Connemara é conhecido por sua versatilidade e habilidades em várias disciplinas equestres. Eles têm uma pelagem geralmente cinza, castanha ou preta e são famosos por sua resistência."
        },
        {
            "Id": 9,
            "Imagem": "https://i.pinimg.com/736x/f5/07/85/f507855858bc8061c2a6965ccfefe10f.jpg",
            "Descricao": "Cavalo Tennessee Walker: Conhecido por seu andar suave e confortável, o Tennessee Walker é uma raça americana que se destaca em competições de marcha. Eles são frequentemente usados para passeios longos devido ao seu andar suave."
        },
        {
            "Id": 10,
            "Imagem": "https://www.comprerural.com/wp-content/uploads/2020/10/Eletric-Bueno-Spark.jpg",
            "Descricao": "Cavalo Quarter Horse: Famoso por sua velocidade em distâncias curtas e habilidades em competições de rodeio. Eles têm um corpo musculoso e ágil."
        },
        {
            "Id": 11,
            "Imagem": "https://i.pinimg.com/474x/10/67/f5/1067f5aa5ae745902a177259c621a35e.jpg",
            "Descricao": "Cavalo Berbere: Originário do Norte da África, o Cavalo Berbere é conhecido por sua resistência e agilidade. É uma raça antiga, adaptada a terrenos áridos e climas extremos."
        }]


    return (
        <div className="container">
            <h2 className="title">Conheça alguns dos animais mais lindos deste mundo de Deus!!!</h2>
            <div className="horse-list">
                {data.map((horse) => (
                    <div key={horse.Id} className="horse-item">
                        <img src={horse.Imagem} alt={`Cavalo ${horse.Id}`} />
                        <p>{horse.Descricao}</p>
                    </div>
                ))}
            </div>

        </div>

    );
}
