import React, { useState, useEffect } from 'react';
import { Link, Outlet } from 'react-router-dom';
import '../../../src/sidebar.css';

const Layout = () => {
  const [isOpen, setIsOpen] = useState(true);
  const [windowSize, setWindowSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const cssMenu = isOpen ? 'toggle-btn' : 'toggle-btn-closed';

  const cssCardapio = isOpen ? 'imgMenu' : 'imgMenu-closed';

  useEffect(() => {
    setWindowSize({
      width: window.innerWidth,
      height: window.innerHeight,
    });

  }, []);

  console.log(windowSize);

  return (
    <>
      <div className={`sidebar ${isOpen ? 'open' : ''}`}>
        <img src={require('../../../src/assets/cardapio.png')} alt="Logo" onClick={toggleSidebar} className={cssCardapio} />
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/details">Horses</Link>
            </li>
            <li>
              <Link to="/about">About</Link>
            </li>
          </ul>
        </nav>
      </div>

      <div className="content">
        <Outlet />
      </div>
    </>
  );
};

export default Layout;
